/**
* callback function for progress bar.
*/
function progress_bar(e) {
    var done = e.position || e.loaded;
    var total = e.totalSize || e.total;
    var per = (Math.floor(done / total * 1000) / 10);
    $('div#progress > div.bar').css('width', per + '%').text(per + ' %');
}

