$(document).ready(function () {
    loader.hide();
    load_zoom_img();
    $(document).on('submit', '#form-zero',function() {
        return false;
    });
    $(document).on('submit', '.modalnoajax form',function() {
        return false;
    });
    $(document).on('click', '#FORM_DELETE button[type="submit"]', function(event) {
        unsaved = false;
        if(Modernizr.history){
            var form_data = $('#FORM_DELETE form').serializeArray();
            form_data.push( {'name':$(this).attr('name')});
            loadContent($('#FORM_DELETE form').attr('action'), form_data);
        }
        $('#FORM_DELETE').modal('hide');
    });
    $(document).on('click', '#form-bloque-botones button[type="submit"]', function() {
        // update_collection_order();
        CKupdate();
        unsaved = false;
        if(Modernizr.history){
            var form_data = $('#form-zero').serializeArray();
            form_data.push( {'name':$(this).attr('name')});
            loadContent($('#form-zero').attr('action'), form_data);
        }
    });

    window.onbeforeunload = function (e) {
        if (unsaved) {
            var message = msg_warning_form_no_saved.replace('<br>','\n'), e = e || window.event;
            if (e) {
                e.returnValue = message;
            }
            return message;
        }
    }

    // filebrowser
    $(document).on('click', '.open-filebrowser', function(e) {
        e.preventDefault();
        var retornar = $(this).attr('data-return');
        $('table#filemanager').attr('data-retornar', retornar);
        var ruta = $('#'+retornar).val().replace('uploads','').split('/');
        ruta.splice(-1,1);
        ruta = ruta.join('/');
        browse(ruta);
    });

    // block change password
    var link_cp = $('#FOSChangePasswordButton'),
        link_cp_href = link_cp.attr('href'),
        modal_cp = $('#FOSChangePassword');
    link_cp.click(function(e) {
        e.preventDefault();
        modal_cp.find('.modal-content').load(link_cp_href, function() {
            modal_cp.modal('show');
        });
    });
    modal_cp.on('submit', 'form', function(e) {
        e.preventDefault()
        var form_data = modal_cp.find('form').serializeArray();
        modal_cp.find('.modal-content').load(link_cp_href, form_data, function(response, status, xhr) {
            if (response == 'correcto') {
                modal_cp.modal('hide');
                new PNotify({
                    text: 'Contrasea actualizada',
                    type: 'success',
                    icon: false
                });
            }
        });
        return false;
    });
    if(Modernizr.history){
        $(document).on('click', '.control-ajax a',function() {
            var _link = $(this).attr("href");

            if ($(this).hasClass('no-ajax')) {
                return true;
            }
            if ( _link != '#' ) {
                if (unsaved) {
                    getConfirm(msg_warning_form_no_saved, function(result) {
                        if ( result ) {
                            history.pushState(null, null, _link);
                            loadContent(_link, false);
                            unsaved = false;
                        }
                    });
                }else{
                    history.pushState(null, null, _link);
                    loadContent(_link, false);
                }
                return false;
            }
            return true;
        });
    }
});

$(document).bind("location_changed", function(event, object) {
    var actual = $('#'+$(object).attr('id')),
        texto = actual.find('.gllpSearchField').val(),
        latitud = actual.find('.gllpLatitude').val(),
        longitud = actual.find('.gllpLongitude').val(),
        zoom = actual.find('.gllpZoom').val(),
        datageo = texto+'||'+latitud+'||'+longitud+'||'+zoom;

    unsaved = true;
    actual.find('.datamap').prop('value', datageo)
    actual.find('.datamap').attr('value', datageo)
    actual.find('.datamap').val(datageo);
});

$(document).on('keypress', '.gllpSearchField', function(e) {
    if(e.which == 13) {
        $(this).parent().find('.gllpSearchButton').trigger('click');
        return false;
    }
});

$(document).on('keypress', '.gllpLatitude, .gllpLongitude, .gllpZoom', function(e) {
    if(e.which == 13) {
        $(this).parent().parent().parent().find('.gllpUpdateButton').trigger('click');
        return false;
    }
});

function corezerodatePicker(campo, formato){
    if (formato != '') {
        var options = {
            format: formato,
            locale: 'es',
            icons: {
                time: 'fa fa-clock-o',
                date: 'fa fa-calendar',
                up: 'fa fa-chevron-up',
                down: 'fa fa-chevron-down',
                previous: 'fa fa-chevron-left',
                next: 'fa fa-chevron-right',
                today: 'fa fa-screenshot',
                clear: 'fa fa-trash',
                close: 'fa fa-remove'
            }
        }
    }else{
        var options = {
            locale: 'es',
            icons: {
                time: 'fa fa-clock-o',
                date: 'fa fa-calendar',
                up: 'fa fa-chevron-up',
                down: 'fa fa-chevron-down',
                previous: 'fa fa-chevron-left',
                next: 'fa fa-chevron-right',
                today: 'fa fa-screenshot',
                clear: 'fa fa-trash',
                close: 'fa fa-remove'
            }
        }
    }

    $('#'+campo).datetimepicker(options);
}

function corezeroselect2(campo){
    $(".select2_"+campo).select2({
        language: "es",
        tags: true,
        tokenSeparators: [',']
    });
    $(".select2-container--default").css('width', '100%');
    $(".select2_"+campo).on('change', function(){
        var taggit = '';
        $(this).find('option:selected').each(function(index, el) {
            taggit += $(this).text()+',';
        });
        $('#'+campo).val(taggit);
    });
}
